sys_script_client.script:
function onCellEdit(sysIDs, table, oldValues, newValue, callback) {
  var saveAndClose = true;
 //Type appropriate comment here, and begin script below
 var ga=new GlideAjax('celleditGlide');
	ga.addParam('syssparm_name','validateDates');
	ga.addParam('syssparm_name','sysIDs');
	ga.addParam('syssparm_newValue','newValue');
	ga.getXML(callscriptInclude);

	function callscriptInclude(response){
		var answer=response.responseXML.documentElement.getAttribute("answer");
		if(answer=='submitAction'){
			var confirmation=confirm("are you sure you want to change start date");
			callback(confirmation);
		}
		else if(answer=='AbortAction'){
			alert("start date must not be greather than end date");
			callback(false);
		}

	}



 callback(saveAndClose); 
}
