sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }

    var pr = g_form.getReference('admission_number', populate);

    function populate(pr) {
        if (pr.patient_name, pr.symptoms, pr.age, pr.contact_no,
            pr.address, pr.date_of_join, pr.sex, pr.emergency) {
            g_form.setValue('patient_name', pr.patient_name);
            g_form.setValue('symptoms', pr.symptoms);
            g_form.setValue('date_of_join', pr.date_of_join);
            g_form.setValue('age', pr.age);
            g_form.setValue('contact_no', pr.contact_no);
            g_form.setValue('address', pr.address);
            g_form.setValue('sex', pr.sex);
            g_form.setValue('emergency', pr.emergency);
        }
    }


    //Type appropriate comment here, and begin script below

}
